Route::group(['prefix' => 'agenda'], function(){

    Route::get('', 'AgendaController@index')->name('agenda.index');
    Route::get('agregar', 'AgendaController@create')->name('agenda.agregar');
    Route::post('agregar', 'AgendaController@store')->name('agenda.agregar');
    Route::get('editar/{id?}', 'AgendaController@edit')->name('agenda.editar');
    // Route::post('editar/{id?}', 'AgendaController@update')->name('agenda.editar');
    // Route::put('editar/{id?}', 'AgendaController@update')->name('agenda.editar');
    Route::put('editar', 'AgendaController@update')->name('agenda.editar');
    Route::get('ver/{id}', 'AgendaController@show')->name('agenda.ver');
    Route::get('eliminar/{id?}', 'AgendaController@destroy')->name('agenda.eliminar');
    
    Route::put('activar', 'AgendaController@activar')->name('agenda.activar');
    Route::put('desactivar', 'AgendaController@desactivar')->name('agenda.activar');
    

});

